        iType = 0
    end

    timer.Destroy( "OnePrint_NotifTimer" )

    if ePrinter.dNotif and IsValid( ePrinter.dNotif ) then
        ePrinter.dNotif:Update( sText, iType )
    else
        local iImgH = ( ePrinter.dPrinter:GetTall() * .032 )

