			if not isnumber( tax ) then
				ply:ChatPrint( "You must insert a number to update the taxes." )
				return
			end
			
			if tax <= 0 then
				ply:ChatPrint( "The tax must be a positive number." )
				return
			end
			
			-- Update the taxes for this team
			net.Start( "CH_Mayor_Net_UpdateTeamTax" )
