	local count = net.ReadUInt(4)
	for i = 1, count do
		local Profiler = net.ReadString()
		local StartedAt = net.ReadFloat()

		local Tbl = GProfiler[Profiler]
		if not Tbl then continue end

		Tbl.ProfileActive = true
		Tbl.Realm = "Server"
		Tbl.StartTime = SysTime() - StartedAt
	end
end)
