	local data = {}
	for i = 1, net.ReadUInt(20) do
		local hookName = net.ReadString()
		data[hookName] = {
			h = net.ReadString(),
			r = hookName,
			c = net.ReadUInt(32),
			t = net.ReadFloat(),
			Source = net.ReadString(),
			Lines = {net.ReadUInt(16), net.ReadUInt(16)}
		}
	end
	GProfiler.Hooks.ProfileData = data
end)

