function LIB.Read(vpath, filetype, callback)
	callback = callback or g_emptyFunction

	if not StreamRadioLib.DataDirectory then
		callback(false, nil)
		return false
	end

	vpath = string.lower(vpath or "")

	if vpath == "" then
		callback(false, nil)
		return false
	end

	if not LIBString.IsValidFilepath(vpath) then
		callback(false, nil)
		return false
	end

	local globalpath = VirtualPathToGlobal(vpath)

	local fs = getFS(filetype)

	if not fs then
		callback(false, nil)
		return false
	end

	if not fs.Read then
		callback(false, nil)
		return false
	end

	if not fs.Exists then
		callback(false, nil)
		return false
	end

	if not fs:Exists(globalpath, vpath) then
		callback(false, nil)
		return false
	end

	return fs:Read(globalpath, vpath, function(success, data)
		if not success then
			callback(false, nil)
			return
		end

		if not data then
			callback(false, nil)
			return
		end

		data = SanitizeData(data)

		if SERVER then
			local urls = table.MemberValuesFromKey(data, "url")
			StreamRadioLib.Whitelist.UpdateFromPlaylist(vpath, urls)
		end

		callback(success, data)
	end)
end

function LIB.Write(vpath, filetype, data, callback)
	callback = callback or g_emptyFunction

	if not StreamRadioLib.DataDirectory then
		callback(false)
		return false
	end

	vpath = string.lower(vpath or "")

	if vpath == "" then
		callback(false)
		return false
	end

	if not LIBString.IsValidFilepath(vpath) then
		callback(false)
		return false
	end

	if not LIBString.IsValidFilepath(vpath) then
		callback(false)
		return false
	end

	local globalpath = VirtualPathToGlobal(vpath)

	if not data then
		callback(false)
		return false
	end

	data = SanitizeData(data)
	local fs = getFS(filetype)

	if not fs then
		callback(false)
		return false
	end

	if not fs.Write then
		callback(false)
		return false
	end

	return fs:Write(globalpath, vpath, data, function(success, ...)
		if SERVER and success then
			local urls = table.MemberValuesFromKey(data, "url")
			StreamRadioLib.Whitelist.UpdateFromPlaylist(vpath, urls)
			StreamRadioLib.Whitelist.InvalidateCache()
		end

		callback(success, ...)
	end)
end

function LIB.Delete(vpath, filetype, callback)
	callback = callback or g_emptyFunction

	if not StreamRadioLib.DataDirectory then
		callback(false)
		return false
	end

	vpath = string.lower(vpath or "")

	if vpath == "" then
		callback(false)
