	if hasGui then
		tmpGui.OnLoadDone = nil
		tmpGui.OnReady = nil
		tmpGui:Remove()
		tmpGui = nil
	end

	if hasGuiMain then
		tmpGuiMain:Remove()
		tmpGuiMain = nil
	end
end

