                a:CreateEntry(true, ashop.L('MoneyToSet'), "UInt32", {
                    required = true,
                }, tonumber(text:GetText()))
                a:Center()

                function a:OnSend(m)
                    net.Start('ashop_Admin_ReceiveInventoryMoney')
                        net.WriteString(s64)
