            ashop.ui.popAskbox(ashop.L('SureToDeleteTrade'), "", function()
                net.Start('ashop_Currency_Delete')
                    net.WriteUInt(objectTarget.id, 8)
                net.SendToServer()
            end)
        end)

        menu:Open()
    end
end)
