        if ( not LocalPlayer():IsTyping() and not gui.IsGameUIVisible() and bind and bind ~= KEY_ESCAPE and ( ( validBind and input.IsKeyDown( bind ) ) or ( input.IsMouseDown( bind ) and not validBind ) ) ) then
