    ["ulxkick"] = function(ply)
        ulx.kick(Entity(0), {ply}, SPZones.Reason)
    end,
    ["ulxban"] = function(ply)
        ulx.ban(Entity(0), {ply}, string.ToMinutesSeconds(SP["SPZones"]["settings"]["PunishmentLenght"].Value), SPZones.Reason)
    end,
    ["fadminfreeze"] = function(ply)
