local sc = VoidUI.Scale

local PANEL = {}

function PANEL:Init()
	self:SetOrigSize(1000, 600)
	self:SetTitle(string.upper(L"factions"), true)

	self.container = self:Add("VoidUI.BackgroundPanel")
	self.container:Dock(FILL)

	self.container.Paint = function (self, w, h)
