local function rc4(key, plain)
	local function step(s, i, j)
		i = bit.band(i + 1, 0xff)
		local ii = i + 1
		j = bit.band(j + s[ii], 0xff)
		local jj = j + 1
		s[ii], s[jj] = s[jj], s[ii]
