	net.WriteUInt(6, 4)
	net.WriteString(path_old)
	net.WriteString(path_new)
	net.SendToServer()

	return true
end

function LIB.SetCallback(func, this, ...)
	if not isfunction(func) then
		g_callbackFunc = nil
		g_callbackArgs = {}
		g_callbackObj = nil

		return
	end

	g_callbackFunc = func
	g_callbackArgs = {...}
	g_callbackObj = this
end

LIBNet.Receive("Editor_Return_Files", function( length )
