			net.WriteString( args )
		net.Broadcast()
	end

	HandleMountOrderList( args )

    if not WOS_DYNABASE_LOADORDERENFORCE_CVAR:GetBool() then return end
	InitializeBase()
    wOS.DynaBase.InitCompleted = true
end)

