            player_pane:SetSpacing( 1 )
            player_pane:EnableVerticalScrollbar( scroll )
            Graphics.PaintBar( player_pane, Color( 30, 30, 30, 220 ), Color( 28, 28, 28 ), Color( 26, 26, 26 ) )
