            local f = 0

            for _, v in pairs(ashop.items) do
                if v.rarity == objectTarget.id then
                    f = f + 1
                end
            end

            if f > 0 then
                ashop.ui.popAskbox(ashop.L('WarningDeleteRankGroup', objectTarget.name, f), ashop.L('CantUndoOperation'), function()
