function RenkLerp(t, ilk, son)
    return Color(
        Lerp(t, ilk.r, son.r),
        Lerp(t, ilk.g, son.g),
        Lerp(t, ilk.b, son.b),
        Lerp(t, ilk.a or 255, son.a or 255)
    )
end

