local function getnewname()
	local newnamebase = "newskin"
	local newname = newnamebase
	local count = 2

	while true do
		if not StreamRadioLib.Skin.IsValidSkinFile(newname) then
			return newname
		end

		if count >= 100 then
			return ""
		end

		newname = newnamebase .. count
		count = count + 1
	end
end

