// Meta extraction
function APP:ExtractMusicTitle(str)
    if str then
        local tbl = string.Explode( "'", str, true)
        for k,v in ipairs(tbl) do
            if v == "StreamTitle=" and tbl[k + 1] != "" then
                return tbl[k + 1]
            end
        end
    end
    return aphone.L("Music_NotFound")
end





