                net.Start("aphone_ChangeRadio")
                    net.WriteUInt(k, 12)
                net.SendToServer()
            end
        end
    end

    if !screenmode then
        main:Phone_DrawTop(main_x, main_y)
    end

    main:aphone_RemoveCursor()
end

