			net.Start( "CH_Mayor_Net_BuyCatalogItem" )
				net.WriteString( k )
			net.SendToServer()
			
			GUI_CatalogFrame:Remove()
		end
		
		
		GUI_CatalogList:AddItem( GUI_ItemPanel )
