	local now = CurTime()
	local timepassed = now - LastThink
	LastThink = now
	
	if not self.DoorNum then
		return
	end
	
	if self.SwitchOn then
		CH_Armory_Locker.Doors[self.DoorNum] = math.Approach( CH_Armory_Locker.Doors[self.DoorNum], 100, SeqSpeed * timepassed )
