function ashop.ExecIfValidData(num, callback)
    local ply = Player(num)

    if IsValid(ply) then
        callback(ply)
    else
        waitingCallbacks[num] = waitingCallbacks[num] or {}
        table.insert(waitingCallbacks[num], callback)
    end
end

