----------------------------]]--

function MinigameObject:LeftClick(Trace, Owner, FirstTime)
    self:Checker(Trace, "table", 1)
    self:Checker(Owner, "player", 2)
end

function MinigameObject:RightClick(Trace, Owner)
    self:Checker(Trace, "table", 1)
    self:Checker(Owner, "player", 2)
end

function MinigameObject:Reload(Trace, Owner)
    self:Checker(Trace, "table", 1)
    self:Checker(Owner, "player", 2)
end

