local stored = {}

function onyx.scoreboard.GetBricksGangName(gangID)
    return (stored[gangID] or '')
end

net.Receive('onyx.scoreboard(Bricks.Gangs):Replace', function(len)
    local id = net.ReadUInt(16)
    local name = net.ReadString()
    stored[id] = name
end)

