                    item:Remove()
                    networkAdminData(2, k)
                end)
                :setText(slib.getLang("sreward", sReward.config["language"], "this_delete", item.data.name))
            end)
            item.addButton(nil, settings_ico, function()
                manageStoreItem(k)
            end, true)

            iteration = iteration + 1
        end

        ignoreResize = false
    end

