        local width = store_canvas:GetWide()
        if store_canvas.oldWide == width then return end
        store_canvas.oldWide = width

        if !IsValid(store_canvas) then return end
        for k,v in ipairs(store_canvas:GetChildren()) do
            v.InvalidateLayout()
        end
    end

